function changePrice(contentsName){$.getJSON("https://cdn.shirai-store.net/assets/json/feature/"+contentsName+"_v1_0.json",(function(data){$(".changePrice").each((function(){var product=$(this).data("products"),priceText,result=data.find(u=>u.productUrl===product);if(result){var sellingPrice=result.sellingPrice,normalPrice=result.normalPrice;priceText=sellingPrice<normalPrice?'<span class="mark-sale">SALE</span><span class="normalPrice">¥'+normalPrice.toLocaleString()+'（税込）</span><span class="sellingPrice salePrice">¥'+sellingPrice.toLocaleString()+"（税込）</span>":'<span class="sellingPrice">¥'+sellingPrice.toLocaleString()+"（税込）</span>",$(this).html(priceText)}}))}))}$((function(){var url=location.href;url.indexOf("/magazine/school-bag")>-1?changePrice("magazine-school-bag"):url.indexOf("/magazine/cabinet-stylish")>-1?changePrice("cabinet-stylish"):url.indexOf("/magazine/chest-stylish")>-1?changePrice("chest-stylish"):url.indexOf("/magazine/compact-tvboard")>-1?changePrice("compact-tvboard"):url.indexOf("/magazine/kitchen-strage-stylish")>-1?changePrice("kitchen-strage-stylish"):url.indexOf("/magazine/newLife")>-1&&changePrice("magazine-newLife")}));